fn main() {
    let m: u32 = get_input().split_ascii_whitespace().last().unwrap().parse().unwrap();
    let mut children: Vec<u32> = get_input().split_ascii_whitespace().map(|v| v.parse().unwrap()).collect();

    let res = children.iter().enumerate().max_by(|x, y| x.1.cmp(y.1)).unwrap().0 + 1;

    println!("{}", res);
}


fn get_input() -> String {
    let mut buffer = String::new();
    std::io::stdin().read_line(&mut buffer).expect("Failed");
    buffer.trim().to_owned()
}

fn get_input_usize() -> usize {
    get_input().parse().unwrap()
}
